const fs = require('fs')

const file = `${__dirname}/processlist_${process.versions.electron ? 'electron' : 'node'}_${process.arch}_${process.versions.modules}.node`
if(!fs.existsSync(file))
    throw Error(`process-list: No build found (platform=${process.versions.electron ? 'electron' : 'node'}, arch=${process.arch}, modulesVer=${process.versions.modules})`)

module.exports = require(file)
